<?php// Группы.$arGroups = array();$arFilter = array('ACTIVE' => 'Y');if ($md->SHOW_SUPER_ADMIN_GROUP_RIGHTS != 'Y') {	$arFilter['ADMIN'] = 'N';}$res = CGroup::GetList($b="sort", $o="asc", $arFilter);while ($arGroup = $res->Fetch()) {	$arGroup['NAME'] = htmlspecialcharsbx($arGroup['NAME']);	$arGroups[$arGroup['ID']] = $arGroup;}// Статусы.$arStatuses = LinemediaAutoOrder::getStatusesList();if (CModule::IncludeModule('sale')) {	// Типы плательщиков.	$arPersonTypes = array();	$arFilter = array('ACTIVE' => 'Y');	$res = CSalePersonType::GetList(array('SORT' => 'ASC', 'NAME' => 'ASC'), $arFilter);	while ($arPersonType = $res->Fetch()) {		$arPersonType['NAME'] = htmlspecialcharsbx($arPersonType['NAME']);		$arPersonTypes[$arPersonType['ID']] = $arPersonType;	}		// Платежные системы.	$arPaySystems = array();	$arFilter = array('ACTIVE' => 'Y');	$res = CSalePaySystem::GetList(array('SORT' => 'ASC', 'NAME' => 'ASC'), $arFilter);	while ($arPaySystem = $res->Fetch()) {		$arPaySystem['NAME'] = htmlspecialcharsbx($arPaySystem['NAME']);		$arPaySystems[$arPaySystem['ID']] = $arPaySystem;	}}?><tr>	<td width="50%" valign="top">		<label for="LM_AUTO_WHOLESALE_STATUS">            <?= GetMessage('LM_AUTO_WHOLESALE_STATUS') ?>        </label>    </td>		<td>	    <? if (!empty($arStatuses)) { ?>	        <? $LM_AUTO_WHOLESALE_STATUS = COption::GetOptionString($sModuleId, 'LM_AUTO_WHOLESALE_STATUS'); ?>	        <select style="width: 60%;" id="LM_AUTO_WHOLESALE_STATUS" name="LM_AUTO_WHOLESALE_STATUS">                <option><?=GetMessage('LM_AUTO_MAIN_STATUS_NOT_IN_USE')?></option>	            <? foreach ($arStatuses as $arStatus) { ?>					<option value="<?= $arStatus["ID"] ?>" <?= ($arStatus['ID'] == $LM_AUTO_WHOLESALE_STATUS) ? ('selected="selected"') : ('') ?>>						<?= $arStatus['NAME'] ?> [<?= $arStatus['ID'] ?>]					</option>	            <? } ?>	        </select>        <? } else { ?>        	<?= GetMessage('LM_AUTO_MAIN_GLOBAL_MENU_HIDE_STORE_NO_STATUSES') ?>        <? } ?>	</td></tr><tr>	<td width="50%" valign="top">		<label for="LM_AUTO_WHOLESALE_GROUP">            <?= GetMessage('LM_AUTO_WHOLESALE_GROUP') ?>        </label>    </td>		<td>	    <? if (!empty($arGroups)) { ?>	        <? $LM_AUTO_WHOLESALE_GROUP = intval(COption::GetOptionInt($sModuleId, 'LM_AUTO_WHOLESALE_GROUP')); ?>	        <select style="width: 60%;" id="LM_AUTO_WHOLESALE_GROUP" name="LM_AUTO_WHOLESALE_GROUP">                <option><?=GetMessage('LM_AUTO_MAIN_STATUS_NOT_IN_USE')?></option>	            <? foreach ($arGroups as $arGroup) { ?>					<option value="<?= $arGroup["ID"] ?>" <?= ($arGroup['ID'] == $LM_AUTO_WHOLESALE_GROUP) ? ('selected="selected"') : ('') ?>>						<?=$arGroup['NAME'] ?> [<?= $arGroup['ID'] ?>]					</option>	            <? } ?>	        </select>        <? } else { ?>        	<?= GetMessage('LM_AUTO_MAIN_GLOBAL_MENU_HIDE_STORE_NO_GROUPS') ?>        <? } ?>	</td></tr><tr>	<td width="50%" valign="top">		<label for="LM_AUTO_WHOLESALE_PERSON">            <?= GetMessage('LM_AUTO_WHOLESALE_PERSON') ?>        </label>    </td>		<td>	    <? if (!empty($arPersonTypes)) { ?>	        <? $LM_AUTO_WHOLESALE_PERSON = intval(COption::GetOptionInt($sModuleId, 'LM_AUTO_WHOLESALE_PERSON')); ?>	        <select style="width: 60%;" id="LM_AUTO_WHOLESALE_PERSON" name="LM_AUTO_WHOLESALE_PERSON">                <option><?=GetMessage('LM_AUTO_MAIN_STATUS_NOT_IN_USE')?></option>	            <? foreach ($arPersonTypes as $arPersonType) { ?>					<option value="<?= $arPersonType["ID"] ?>" <?= ($arPersonType['ID'] == $LM_AUTO_WHOLESALE_PERSON) ? ('selected="selected"') : ('') ?>>						<?= $arPersonType['NAME'] ?> [<?= $arPersonType['ID'] ?>]					</option>	            <? } ?>	        </select>        <? } else { ?>        	<?= GetMessage('LM_AUTO_MAIN_GLOBAL_MENU_HIDE_STORE_NO_PERSON_TYPES') ?>        <? } ?>	</td></tr><tr>	<td width="50%" valign="top">		<label for="LM_AUTO_WHOLESALE_PAYSYSTEM">            <?= GetMessage('LM_AUTO_WHOLESALE_PAYSYSTEM') ?>        </label>    </td>		<td>	    <? if (!empty($arPaySystems)) { ?>	        <? $LM_AUTO_WHOLESALE_PAYSYSTEM = intval(COption::GetOptionInt($sModuleId, 'LM_AUTO_WHOLESALE_PAYSYSTEM')); ?>	        <select style="width: 60%;" id="LM_AUTO_WHOLESALE_PAYSYSTEM" name="LM_AUTO_WHOLESALE_PAYSYSTEM">                <option><?=GetMessage('LM_AUTO_MAIN_STATUS_NOT_IN_USE')?></option>	            <? foreach ($arPaySystems as $arPaySystem) { ?>					<option value="<?= $arPaySystem["ID"] ?>" <?= ($arPaySystem['ID'] == $LM_AUTO_WHOLESALE_PAYSYSTEM) ? ('selected="selected"') : ('') ?>>						<?= $arPaySystem['NAME'] ?> [<?= $arPaySystem['ID'] ?>]					</option>	            <? } ?>	        </select>        <? } else { ?>        	<?= GetMessage('LM_AUTO_MAIN_GLOBAL_MENU_HIDE_STORE_NO_PAYSYSTEMS') ?>        <? } ?>	</td></tr>